// Обробка JSON даних з Python скрипта
var data = msg.payload.toString().trim();

try {
    // Парсинг JSON
    var joystickData = JSON.parse(data);

    if (joystickData.axes && joystickData.buttons) {
        msg.payload = {
            axes: joystickData.axes,
            buttons: joystickData.buttons
        };
        return msg;
    }
} catch (e) {
    // Якщо не JSON, ігноруємо
    node.warn("Не можу парсити JSON: " + data);
}

return null;

// Покращена обробка даних з TX12
if (msg.payload && msg.payload.axes && msg.payload.buttons) {
    var commands = [];
    var axes = msg.payload.axes;
    var buttons = msg.payload.buttons;

    // Константи для налаштування
    var DEAD_ZONE = 3000;  // Мертва зона для осей
    var SERVO_MIN = 0;     // Мінімальний кут сервоприводу
    var SERVO_MAX = 180;   // Максимальний кут сервоприводу

    // Функція для перетворення значення осі в кут сервоприводу
    function axisToServo(axisValue) {
        // Обмежуємо значення
        axisValue = Math.max(-32768, Math.min(32767, axisValue));

        // Перетворюємо в діапазон 0-180
        var angle = Math.round(((axisValue + 32768) / 65535) * (SERVO_MAX - SERVO_MIN) + SERVO_MIN);

        // Обмежуємо результат
        return Math.max(SERVO_MIN, Math.min(SERVO_MAX, angle));
    }

    // Збереження попередніх значень в контексті
    var lastValues = flow.get('lastServoValues') || [90, 90, 90, 90];
    var hasChanged = false;

    // Обробка осей з мертвою зоною та фільтрацією змін
    for (var i = 0; i < 4; i++) {
        if (Math.abs(axes[i]) > DEAD_ZONE) {
            var newAngle = axisToServo(axes[i]);

            // Перевіряємо чи змінилося значення більше ніж на 2 градуси
            if (Math.abs(newAngle - lastValues[i]) > 2) {
                commands.push("S" + (i + 1) + ":" + newAngle);
                lastValues[i] = newAngle;
                hasChanged = true;
            }
        }
    }

    // Зберігаємо нові значення
    if (hasChanged) {
        flow.set('lastServoValues', lastValues);
    }

    // Обробка кнопок (з антибрязканням)
    var lastButtons = flow.get('lastButtons') || [0, 0, 0, 0];

    for (var i = 0; i < 4; i++) {
        if (buttons[i] === 1 && lastButtons[i] === 0) {
            // Кнопка щойно натиснута
            switch (i) {
                case 0:
                    commands.push("GRIP:OPEN");
                    break;
                case 1:
                    commands.push("GRIP:CLOSE");
                    break;
                case 2:
                    commands.push("HOME");
                    break;
                case 3:
                    commands.push("READY");
                    break;
            }
        }
    }

    // Зберігаємо стан кнопок
    flow.set('lastButtons', buttons.slice(0, 4));

    // Відправляємо команди якщо є зміни
    if (commands.length > 0) {
        msg.payload = commands.join(";") + "\n";

        // Додаємо інформацію для налагодження
        msg.debug = {
            axes: axes,
            buttons: buttons,
            commands: commands,
            timestamp: new Date().toISOString()
        };

        return msg;
    }
}

// Якщо немає змін, не відправляємо нічого
return null;
